import React, { useState } from 'react';

import CoursesContext, { Course, Goal } from './courses-context';

const CoursesContextProvider: React.FC = (props) => {
  const [courses, setCourses] = useState<Course[]>([
    {
      id: 'c1', 
      title: 'React - The Complete Guide', 
      enrolled: new Date(),
      goals: []
    }
  ]);

  const addCourse = (title: string, date: Date) => {
    const newCourse: Course = {
      id: Math.random().toString(), 
      title, 
      enrolled: date, 
      goals: []
    };
    setCourses(prev => prev.concat(newCourse));
  };

  const addGoal = (courseId: string, text: string) => {
    const newGoal: Goal = {
      id: Math.random().toString(),
      text
    };

  };

  const deleteGoal = () => {};

  const updateGoal = () => {};

  return <CoursesContext.Provider value={{courses, addGoal, addCourse, deleteGoal, updateGoal}} >{props.children}</CoursesContext.Provider>;
};

export default CoursesContextProvider;
