import React from 'react';
import { IonHeader, IonContent, IonToolbar, IonTitle, IonButton, IonPage, IonGrid, IonRow, IonCol, IonCard, IonCardContent } from '@ionic/react';
import { useHistory } from 'react-router-dom';

export const COURSE_DATA = [
  { id: 'c1', title: 'Ionic + React - The Practical Guide' }, 
  { id: 'c2', title: 'React - The Complete Guide' }, 
  { id: 'c3', title: 'JavaScript - The Complete Guide' }
];

const Courses: React.FC = () => {
  // const history = useHistory();
  // const changePageHandler = () => {
  //   history.push('/course-goals')
  // };

  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonTitle>
            Courses
          </IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent>
        <IonGrid>
          {COURSE_DATA.map(course => (
            <IonRow key={course.id}>
              <IonCol size-md='4' offset='4'>
                <IonCard>
                  <IonCardContent className="ion-text-center">
                    <h2>{course.title}</h2>
                    <IonButton routerLink={`/courses/${course.id}`}>View Course Goals</IonButton>
                  </IonCardContent>
                </IonCard>
              </IonCol>
            </IonRow>
          ))}
        </IonGrid>
      </IonContent>
    </IonPage>
  );
};

export default Courses;
