import { IonBackButton, IonButtons, IonContent, IonHeader, IonItem, IonLabel, IonList, IonPage, IonTitle, IonToolbar } from '@ionic/react';
import React from 'react';
import { useParams } from 'react-router-dom';
import { COURSE_DATA } from './Courses';

const CourseGoals: React.FC = () => {
  const selectedCourseId = useParams<{ courseId: string }>().courseId;

  const selectedCourse = COURSE_DATA.find(c => c.id === selectedCourseId);

  const deleteItemHandler = () => {
    console.log("Deleted...");
  };

  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonButtons slot="start">
            <IonBackButton defaultHref="/" />
          </IonButtons>
          <IonTitle>{selectedCourse ? selectedCourse.title : 'No course found!'}</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent>
        {selectedCourse && <IonList>
          {selectedCourse.goals.map(goal => (
            <IonItem key={goal.id} lines="full" button onClick={deleteItemHandler}>
              <IonLabel>
                {goal.text}
              </IonLabel>
            </IonItem>))}
        </IonList>}
      </IonContent>
    </IonPage>
  );

};

export default CourseGoals;
