import {
  IonBackButton,
  IonButton,
  IonButtons,
  IonContent,
  IonFab,
  IonFabButton,
  IonHeader,
  IonIcon,
  IonItem,
  IonItemOption,
  IonItemOptions,
  IonItemSliding,
  IonLabel,
  IonList,
  IonPage,
  IonTitle,
  IonToolbar,
} from "@ionic/react";
import { addOutline, create, trash } from 'ionicons/icons';
import React from "react";
import { useParams } from "react-router-dom";
import { COURSE_DATA } from "./Courses";

const CourseGoals: React.FC = () => {
  const selectedCourseId = useParams<{ courseId: string }>().courseId;

  const selectedCourse = COURSE_DATA.find((c) => c.id === selectedCourseId);

  const deleteItemHandler = () => {
    console.log("Deleted...");
  };

  const startEditGoalHandler = (event: React.MouseEvent) => {
    event.stopPropagation();
    console.log("Edited...");
  };

  const startAddGoalHandler = () => {
    console.log("Adding goal...");
  };

  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonButtons slot="start">
            <IonBackButton defaultHref="/courses/list" />
          </IonButtons>
          <IonTitle>
            {selectedCourse ? selectedCourse.title : "No course found!"}
          </IonTitle>
          <IonButtons slot="end">
            <IonButton onClick={startAddGoalHandler} >
              <IonIcon slot="icon-only" icon={addOutline} />
            </IonButton>
          </IonButtons>
        </IonToolbar>
      </IonHeader>
      <IonContent>
        {selectedCourse && (
          <IonList>
            {selectedCourse.goals.map((goal) => (
              <IonItemSliding key={goal.id} >
                <IonItemOptions side="start" >
                  <IonItemOption onClick={deleteItemHandler} color="danger" >
                    <IonIcon slot="icon-only" icon={trash} />
                  </IonItemOption>
                </IonItemOptions>
                <IonItem
                  lines="full"
                  // button
                  // onClick={deleteItemHandler}
                >
                  <IonLabel>{goal.text}</IonLabel>
                  {/* <IonButton fill="clear" color="dark" slot="end" onClick={startEditGoalHandler} >
                    <IonIcon slot="icon-only" icon={create} />
                  </IonButton> */}
                </IonItem>
                <IonItemOptions side="end" >
                  <IonItemOption onClick={startEditGoalHandler} >
                    <IonIcon slot="icon-only" icon={create} />
                  </IonItemOption>
                </IonItemOptions>
              </IonItemSliding>
            ))}
          </IonList>
        )}
        <IonFab horizontal="end" vertical="bottom" >
          <IonFabButton>
            <IonIcon icon={addOutline} />
          </IonFabButton>
        </IonFab>
      </IonContent>
    </IonPage>
  );
};

export default CourseGoals;
