import React from 'react';
import { IonHeader, IonContent, IonToolbar, IonTitle, IonPage, IonButton, IonMenuButton, IonItem } from '@ionic/react';

import { COURSE_DATA } from './Courses';
// import { useHistory } from 'react-router-dom';

const AllGoals: React.FC = () => {
  // const history = useHistory();
  // const changePageHandler = () => {
  //   history.push('/course-goals')
  // };

  const goals = COURSE_DATA.map(course => {
    return course.goals;
  }).reduce((goalArr, nestedGoals) => {
    let updatedGoalArray = goalArr;
    for (const goal of nestedGoals) {
      updatedGoalArray = updatedGoalArray.concat(goal);
    }
    return updatedGoalArray;
  }, []);

  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonButton slot="start">
            <IonMenuButton />
          </IonButton>
          <IonTitle>
            All Goals
          </IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent>
        {goals.map(goal => (
        <IonItem key={goal.id}>
          {goal.text}
        </IonItem>))}
      </IonContent>
    </IonPage>
  );
};

export default AllGoals;
