import React from 'react';
import { IonHeader, IonContent, IonToolbar, IonTitle, IonPage, IonButton, IonMenuButton } from '@ionic/react';

import { COURSE_DATA } from './Courses';
// import { useHistory } from 'react-router-dom';

const AllGoals: React.FC = () => {
  // const history = useHistory();
  // const changePageHandler = () => {
  //   history.push('/course-goals')
  // };

  const goals = COURSE_DATA.map(course => {
    return course.goals;
  }).reduce((goalArr, nestedGoals) => {
    const updatedGoalArray = goalArr;
    for (let goal of nestedGoals) {
      updatedGoalArray = updatedGoalArray.concat(goal);
    }
    return updatedGoalArray;
  }, []);

  console.log(goals);

  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonButton slot="start">
            <IonMenuButton />
          </IonButton>
          <IonTitle>
            All Goals
          </IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent>
        <h2>This works - all goals page!</h2>
      </IonContent>
    </IonPage>
  );
};

export default AllGoals;
