import React, { useContext } from 'react';
import { IonHeader, IonContent, IonToolbar, IonTitle, IonPage, IonButton, IonMenuButton, IonItem, IonLabel } from '@ionic/react';
import CoursesContext from '../data/courses-context';

// import { useHistory } from 'react-router-dom';

const AllGoals: React.FC = () => {
  // const history = useHistory();
  // const changePageHandler = () => {
  //   history.push('/course-goals')
  // };
  const coursesCtx = useContext(CoursesContext);

  const goals = coursesCtx.courses.map(course => {
    return course.goals.map(goal => {
      return { ...goal, courseTitle: course.title };
    });
  }).reduce((goalArr, nestedGoals) => {
    let updatedGoalArray = goalArr;
    for (const goal of nestedGoals) {
      updatedGoalArray = updatedGoalArray.concat(goal);
    }
    return updatedGoalArray;
  }, []);

  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonButton slot="start">
            <IonMenuButton />
          </IonButton>
          <IonTitle>
            All Goals
          </IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent>
        {goals.map(goal => (
        <IonItem key={goal.id}>
          <IonLabel>
            <h2>{goal.text}</h2>
            <p>{goal.courseTitle}</p>
          </IonLabel>
        </IonItem>))}
      </IonContent>
    </IonPage>
  );
};

export default AllGoals;
